from HEBEI.TT_LR_COMPANY


        and INSTANCEID = #{instanceid}


        and COMPANY_ID = #{companyId}


        and REQUEST_NO = #{requestNo}


        and RESPONSE_NO = #{responseNo}


        and PASS_NO = #{passNo}


        and NAME = #{name}


