        and DISTRICT = #{district}


        and REGIST_ADDR = #{registAddr}


        and REGIST_ADDR_POST = #{registAddrPost}


        and CONTACT_ADDR = #{contactAddr}


        and CONTACT_ADDR_POST = #{contactAddrPost}


        and CONTACT_PERSON = #{contactPerson}


        and CONTACT_PHONE = #{contactPhone}


        and LONGITUDE = #{longitude}


        and LONGITUDE_HOUR = #{longitudeHour}


        and LONGITUDE_SECOND = #{longitudeSecond}


        and LATITUDE = #{latitude}


        and LATITUDE_HOUR = #{latitudeHour}


        and LATITUDE_SECOND = #{latitudeSecond}


        and LEGAL_PERSON = #{legalPerson}


        and LEGAL_PERSON_TYPE = #{legalPersonType}


        and LEGAL_PERSON_ID = #{legalPersonId}


        and LEGAL_PERSON_PHONE = #{legalPersonPhone}


        and KIND = #{kind}


        and INDUSTRY_TYPE = #{industryType}


        and NUCLIDE_PRODUCE_ACTIVE = #{nuclideProduceActive}


        and NUCLIDE_SALE_ACTIVE = #{nuclideSaleActive}


        and NUCLIDE_USE_ACTIVE = #{nuclideUseActive}


        and RADIAL_PRODUCE_ACTIVE = #{radialProduceActive}


        and RADIAL_SALE_ACTIVE = #{radialSaleActive}


        and RADIAL_USE_ACTIVE = #{radialUseActive}


        and RADIAL_SALE_AND_PRODUCE = #{radialSaleAndProduce}


        and NON_SEAL_ACTIVE = #{nonSealActive}


        and NON_SEAL_TYPE = #{nonSealType}


        and USE_AND_COLLECTION = #{useAndCollection}


        and RADIAL_ORGNAME = #{radialOrgname}


        and RADIAL_ORGCONTACT_PERSON = #{radialOrgcontactPerson}


        and RADIAL_ORGCONTACT_PHONE = #{radialOrgcontactPhone}


        and RADIAL_ORGCONTACT_MOBILE = #{radialOrgcontactMobile}


        and RADIAL_ORGCONTACT_FAX = #{radialOrgcontactFax}


        and RADIAL_ORGCONTACT_EMAIL = #{radialOrgcontactEmail}


        and ORGANIZATION_CODE = #{organizationCode}


        and REMARK = #{remark}


        and RESPONSE_STATUS = #{responseStatus}


        and PERMISSION = #{permission}


        and IS_DELETE = #{isDelete}


        and INSERT_USER = #{insertUser}


        and INSERT_DATE = #{insertDate}


        and MODIFY_USER = #{modifyUser}


        and MODIFY_DATE = #{modifyDate}


        and IS_APPLY = #{isApply}


        and LICENSE_ISSUE_ORGAN = #{licenseIssueOrgan}


        and LICENSE_STARTDATE = #{licenseStartdate}


        and LICENSE_NO = #{licenseNo}


        and LICENSE_ENDDATE = #{licenseEnddate}


        and LICENCE_ISSUING_AUTHORITY = #{licenceIssuingAuthority}


        and LICENSE_TYPE = #{licenseType}


        and BE_PASS = #{bePass}


        and NOPASS_REASON = #{nopassReason}


        and REQUEST_SOURCE = #{requestSource}


        and REQUEST_SOURCE_ID = #{requestSourceId}


        and MODULE_NUMBER = #{moduleNumber}


        and FAX = #{fax}


        and EMAIL = #{email}


        and COMPANY_TYPE = #{companyType}


        and LICENSE_CONDITION = #{licenseCondition}


